import Web3 from "web3";
//access our local copy to contract deployed on rinkeby testnet
//use your own contract address
import HBL_MARKET from "./contracts/hblMarket.json";
const fs = require("fs");

const address = "0x249Ea4c4cbF74E51309bF15c9b4fF120cf550d6B";
let web3 = new Web3(
  Web3.givenProvider ||
    "wss://ropsten.infura.io/ws/v3/43cda5da85674a7a88c5e531e2a09a76"
);
var parsed = JSON.parse(fs.readFileSync(HBL_MARKET));
var abi = parsed.abi;
//use the ABI from your contract
console.log(abi);
export default new web3.eth.Contract(abi, address);
