import React, { Component, useState } from "react";
import "./App.css";
import { useWeb3 } from "@openzeppelin/network/react";
import Web3 from "web3";
import getWeb3 from "./getWeb3";
import { TODO_LIST_ABI } from "./contracts/hblMarket.json";
import "bootstrap/dist/css/bootstrap.css";
import axios from "axios";
import ipfs from "./ipfs";

const CONTRACT_ADDRESS = "0x249Ea4c4cbF74E51309bF15c9b4fF120cf550d6B";

const infuraProjectId = "43cda5da85674a7a88c5e531e2a09a76";
class App extends Component {
  componentWillMount() {
    this.loadBlockchainData();
  }
  constructor(props) {
    super(props);
    this.state = {
      account: "",
      taskCount: 0,
      tasks: [],
      loading: true,
      selectedFile: null,
      ipfsHash: null,
    };
  }
  async loadBlockchainData() {
    let web3 = new Web3(
      Web3.givenProvider ||
        "wss://ropsten.infura.io/ws/v3/43cda5da85674a7a88c5e531e2a09a76"
    );
    let accounts = await web3.eth.getAccounts();
    this.setState({ account: accounts[0] });
  }
  onFileChange = (event) => {
    // Update the state
    this.setState({ selectedFile: event.target.files[0] });
  };

  onFileUpload = () => {
    // Create an object of formData
    const formData = new FormData();

    // Update the formData object
    formData.append(
      "myFile",
      this.state.selectedFile,
      this.state.selectedFile.name
    );

    // Details of the uploaded file
    console.log(this.state.selectedFile);

    // Request made to the backend api
    // Send formData object
    axios.post("../public", formData);
  };

  fileData = () => {
    if (this.state.selectedFile) {
      return (
        <div>
          <h2>File Details:</h2>
          <p>File Name: {this.state.selectedFile.name}</p>
          <p>File Type: {this.state.selectedFile.type}</p>
          <p>
            Last Modified:{" "}
            {this.state.selectedFile.lastModifiedDate.toDateString()}
          </p>
        </div>
      );
    } else {
      return (
        <div>
          <br />
          <h4>Choose before Pressing the Upload button</h4>
        </div>
      );
    }
  };
  // const web3 = await getWeb3.eth();
  // console.log(web3);
  // const web3Context = useWeb3(
  //   `wss://ropsten.infura.io/ws/v3/${infuraProjectId}`
  // );
  render() {
    return (
      <div className="App" className="container-fluid">
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="http://www.dappuniversity.com/free-download"
          >
            Escrow Payment | Ethereum % IPFS
          </a>
          <ul className="navbar-nav px-3">
            <li className="nav-item text-nowrap d-none d-sm-none d-sm-block">
              <small>
                <a className="nav-link" href="#">
                  <span id="account"></span>
                </a>
              </small>
            </li>
          </ul>
        </nav>
        <br></br>
        <br></br>
        <div>
          <h3 className="text-center"> Product Details </h3>
          <div className="row justify-content-center">
            <form>
              <div className="form-group row">
                <input
                  class="form-control"
                  type="text"
                  placeholder="Product ID"
                  id="product_id"
                />
              </div>
              <div className="form-group row">
                <input
                  class="form-control "
                  type="text"
                  placeholder="Product Name"
                  id="product_name"
                />
              </div>
              <div className="form-group row">
                <input
                  class="form-control"
                  type="text"
                  placeholder="IPFS_Hash"
                  id="ipfs_hash"
                  disabled
                />
              </div>

              <fieldset className="form-group">
                <div className="form-group row">
                  <input
                    class="form-control"
                    type="file"
                    placeholder="File"
                    name="file"
                    onChange={this.onFileChange}
                  />
                  <button
                    type="button"
                    className="btn btn-primary justify-content-center"
                    onClick={this.onFileUpload}
                  >
                    Upload file
                  </button>
                </div>
              </fieldset>
              <div> {this.state.selectedFile ? this.fileData() : ""}</div>

              <div className="form-group row">
                <div className="col-sm-10">
                  <button type="submit" className="btn btn-primary">
                    Sign in
                  </button>
                  <Button onClick={this.onClick}>
                    {" "}
                    Get Transaction Receipt{" "}
