import React, { Component } from "react";
import "./App.css";
import { useWeb3 } from "@openzeppelin/network/react";
import Web3 from "web3";
import getWeb3 from "./getWeb3";
import { TODO_LIST_ABI } from "./contracts/hblMarket.json";
import "bootstrap/dist/css/bootstrap.css";

const CONTRACT_ADDRESS = "0x249Ea4c4cbF74E51309bF15c9b4fF120cf550d6B";

const infuraProjectId = "43cda5da85674a7a88c5e531e2a09a76";
class App extends Component {
  componentWillMount() {
    this.loadBlockchainData();
  }
  constructor(props) {
    super(props);
    this.state = {
      account: "",
      taskCount: 0,
      tasks: [],
      loading: true,
    };
  }
  async loadBlockchainData() {
    let web3 = new Web3(
      Web3.givenProvider ||
        "wss://ropsten.infura.io/ws/v3/43cda5da85674a7a88c5e531e2a09a76"
    );
    let accounts = await web3.eth.getAccounts();
    this.setState({ account: accounts[0] });
  }
  // const web3 = await getWeb3.eth();
  // console.log(web3);
  // const web3Context = useWeb3(
  //   `wss://ropsten.infura.io/ws/v3/${infuraProjectId}`
  // );
  render() {
    return (
      <div className="App" className="container-fluid">
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="http://www.dappuniversity.com/free-download"
          >
            Dapp University | Todo List
          </a>
          <ul className="navbar-nav px-3">
            <li className="nav-item text-nowrap d-none d-sm-none d-sm-block">
              <small>
                <a className="nav-link" href="#">
                  <span id="account"></span>
                </a>
              </small>
            </li>
          </ul>
        </nav>

        <div>
          <p className="text-center">{this.state.account}</p>
          {/* <Web3Data title="Web3 Data" web3Context={web3Context} /> */}
          {/* {console.log(Web3Data)} */}
        </div>
      </div>
    );
  }
}
export default App;
